import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { Apartment } from '../models/apartment.model';
import { ApartmentService } from '../services/apartment.service';

@Component({
  selector: 'app-apartment-list',
  standalone: true,
  imports:[],
  templateUrl: './apartment-list.component.html',
  styleUrl: './apartment-list.component.css'
})
export class ApartmentListComponent implements OnInit {
  apartments$ ?: Observable<Apartment[]>;
  filteredApartments$ ?: Observable<Apartment[]>;
  sortOption: string = 'asc';
  roomFilter: number | null = null;
  constructor(private apartmentService: ApartmentService){

  }

  ngOnInit(): void {
    this.apartments$ = this.apartmentService.getAllApartments();
    this.applyFilters();
  }

  onSortChange(sortOption: string){
    this.sortOption = sortOption;
    this.applyFilters();
  }
  onFilterChange(roomFilter: string){
    this.roomFilter = roomFilter ? +roomFilter : null;
    this.applyFilters();
  }

  applyFilters(){
    let apartments = this.apartments$;

    if(this.roomFilter !== null){
      apartments = apartments.filter(apartment => apartment.Rooms === this.roomFilter);
    }

    if(this.sortOption === 'asc'){
    }
  }
}
