import { Component, EventEmitter, Input, OnDestroy, OnInit, Output } from '@angular/core';
import { ApartmentService } from '../services/apartment.service';
import { UpdateApartmentRequest } from '../models/update-apartment-request.model';
import { FormsModule, NgForm } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { Apartment } from '../models/apartment.model';
import { Subscription } from 'rxjs';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-edit-apartment',
  standalone: true,
  imports: [FormsModule, CommonModule],
  templateUrl: './edit-apartment.component.html',
  styleUrl: './edit-apartment.component.css',
})
export class EditApartmentComponent implements OnInit, OnDestroy {
  @Input() apartmentId: string | null = null;
  @Output() editCompleted = new EventEmitter<void>();
  editApartmentSubscription?: Subscription;
  getApartmentSubscription?: Subscription;
  apartment?: Apartment;

  constructor(
    private route: ActivatedRoute,
    private apartmentService: ApartmentService
  ) {}

  ngOnInit(): void {
    if (this.apartmentId) {
    }
    if (this.apartmentId) {
      console.log('Fetching apartment data for ID:', this.apartmentId);
      // get the data from the API for this category Id
      this.getApartmentSubscription = this.apartmentService
        .getApartmentById(this.apartmentId)
        .subscribe({
          next: (response) => {
            this.apartment = response;
          },
        });
    }
  }

  onFormSubmit(form: NgForm): void {
    if (!this.apartment) return;
    const updateApartmentRequest: UpdateApartmentRequest = {
      Name: this.apartment?.name ?? '',
      Rooms: this.apartment?.rooms ?? 1,
      Price: this.apartment?.price ?? 1,
      Description: this.apartment?.description ?? '',
    };
    //pass the object to service
    if (this.apartmentId) {
      this.editApartmentSubscription = this.apartmentService
        .updateApartment(this.apartmentId, updateApartmentRequest)
        .subscribe({
          next: (response) => {
            this.editCompleted.emit();
          },
        });
    }
  }
  ngOnDestroy(): void {
    this.editApartmentSubscription?.unsubscribe();
    this.getApartmentSubscription?.unsubscribe();
  }
}
<div class="container">
    <h1 class="mt-3">Edit Apartment</h1>

    <ng-container *ngIf="apartment; else notFound">
        <form #form="ngForm" (ngSubmit)="onFormSubmit(form)">
            <div class="row">
                <div class="col-md-6 mb-3">
                    <label class="form-label" for="apartmentId">Apartment Id</label>
                    <input type="text" disabled
                           id="apartmentId"
                           class="form-control"
                           name="apartmentId"
                           value="{{apartment.id}}">
                </div>

                <div class="col-md-6 mb-3">
                    <label class="form-label" for="apartmentName">Apartment Name</label>
                    <input type="text"
                           id="apartmentName"
                           class="form-control"
                           name="apartmentName"
                           [(ngModel)]="apartment.name"
                           required
                           maxlength="99">
                        <div *ngIf="form.submitted && form.controls.apartmentName?.invalid" class="text-danger">
                            <small *ngIf="form.controls.apartmentName?.errors?.required">Apartment name is required.</small>
                            <small *ngIf="form.controls.apartmentName?.errors?.maxlength">Max length is 99 characters.</small>
                        </div>
                </div>

                <div class="col-md-6 mb-3">
                    <label class="form-label" for="apartmentRoomsNumber">Rooms Number</label>
                    <input type="number" 
                           id="apartmentRoomsNumber"
                           class="form-control"
                           name="apartmentRoomsNumber"
                           [(ngModel)]="apartment.rooms"
                           required
                           min="1">
                        <div *ngIf="form.submitted && form.controls.apartmentRoomsNumber?.invalid" class="text-danger">
                            <small *ngIf="form.controls.apartmentRoomsNumber?.errors?.required">Number of rooms is required.</small>
                            <small *ngIf="form.controls.apartmentRoomsNumber?.errors?.min">Must be at least 1.</small>
                        </div>
                </div>

                <div class="col-md-6 mb-3">
                    <label class="form-label" for="apartmentPrice">Price</label>
                    <input type="number" 
                           id="apartmentPrice"
                           class="form-control"
                           name="apartmentPrice"
                           [(ngModel)]="apartment.price"
                           required
                           min="1">
                        <div *ngIf="form.submitted && form.controls.apartmentPrice?.invalid" class="text-danger">
                            <small *ngIf="form.controls.apartmentPrice?.errors?.required">Price is required.</small>
                            <small *ngIf="form.controls.apartmentPrice?.errors?.min">Must be at least 1.</small>
                        </div>
                </div>

                <div class="col-md-12 mb-3">
                    <label class="form-label" for="apartmentDescription">Description</label>
                    <textarea id="apartmentDescription"
                              class="form-control"
                              name="apartmentDescription"
                              [(ngModel)]="apartment.description"
                              rows="3"
                              maxlength="999"></textarea>
                            <div *ngIf="form.submitted && form.controls.apartmentDescription?.errors?.maxlength" class="text-danger">
                                <small>Max length is 999 characters.</small>
                            </div>
                </div>

                <div class="mt-3 d-flex">
                    <button type="submit" class="btn btn-primary" [disabled]="form.invalid">Save</button>
                </div>
            </div>
        </form>
    </ng-container>

    <ng-template #notFound>
        <div class="alert alert-warning" role="alert">
            Apartment not found!
        </div>
    </ng-template>
</div>
