import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { Apartment } from '../models/apartment.model';
import { ApartmentService } from '../services/apartment.service';
import { FormsModule } from '@angular/forms';
@Component({
  selector: 'app-apartment-list',
  standalone: true,
  imports:[],
  templateUrl: './apartment-list.component.html',
  styleUrl: './apartment-list.component.css'
})
export class ApartmentListComponent implements OnInit {
  apartments$?: Observable<Apartment[]>;
  roomsFilter?: number;
  sortOption?: string;
  constructor(private apartmentService: ApartmentService){}

  ngOnInit(): void { 
    this.loadApartments();
  }
  loadApartments(): void {
    this.apartments$ = this.apartmentService.getAllApartments(this.roomsFilter, this.sortOption);
  }
  onSortChange(sortOption: string): void {
    this.sortOption = sortOption;
    this.loadApartments();
  }
  onFilterChange(rooms: number): void {
    this.roomsFilter = rooms;
    this.loadApartments();
  }
  deleteApartment(id: string): void {
    this.apartmentService.deleteApartment(id).subscribe(() => {
      this.loadApartments();
    });
  }
}
