import { Component, OnDestroy, OnInit } from '@angular/core';
import { Observable, Subscription } from 'rxjs';
import { Apartment } from '../models/apartment.model';
import { ApartmentService } from '../services/apartment.service';
import { FormsModule } from '@angular/forms';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-apartment-list',
  standalone: true,
  imports: [FormsModule, CommonModule],
  templateUrl: './apartment-list.component.html',
  styleUrl: './apartment-list.component.css',
})
export class ApartmentListComponent implements OnInit, OnDestroy {
  apartments$?: Observable<Apartment[]>;
  deleteApartmentSubscription: Subscription = new Subscription()
  rooms?: number;
  price: string = 'asc';
  constructor(private apartmentService: ApartmentService) {}
  
  ngOnInit(): void {
    this.loadApartments();
  }
  loadApartments(): void {
    this.apartments$ = this.apartmentService.getAllApartments(
      this.rooms,
      this.price
    );
  }
  onFilterChange(event: any): void {
    this.rooms = event.target.value;
    this.loadApartments();
  }

  onSortChange(event: any): void {
    this.price = event.target.value;
    this.loadApartments();
  }

  deleteApartment(id: string): void {
    this.deleteApartmentSubscription = this.apartmentService.deleteApartment(id).subscribe({
      next: (response) => {
        this.loadApartments();
        return response;
      },
      error: err => {
        console.error('Error deleting apartment:', err);
      }
    });
  }
  applyFilters(): void {
    this.loadApartments();
  }
  ngOnDestroy(): void {
    this.deleteApartmentSubscription.unsubscribe();
  }

}
<div class="m-3">
  <label for="rooms">Filter by rooms:</label>
  <input id="rooms" type="number" [(ngModel)]="rooms" (input)="applyFilters()">

  <label for="price">Sort by price:</label>
  <select id="price" [(ngModel)]="price" (change)="applyFilters()">
    <option value="asc">Price - lowest to highest</option>
    <option value="desc">Price - highest to lowest</option>
  </select>
</div>

<ul class="m-3" >
  <li *ngFor="let apartment of apartments$ | async as apartments; else noData">
    {{apartment.Rooms}} / ${{apartment.Price}} / {{apartment.Name}}
    <button class="btn btn-danger" (click)="deleteApartment(apartment.Id)">Delete</button>
  </li>
</ul>
<ng-template #noData>
  <p>No apartments available</p>
</ng-template>
