import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { ApartmentService } from '../services/apartment.service';
import { v4 as uuidv4 } from 'uuid';

@Component({
  selector: 'app-apartment-form',
  standalone: true,
  imports: [],
  templateUrl: './apartment-form.component.html',
  styleUrl: './apartment-form.component.css'
})
export class ApartmentFormComponent {
  constructor(private apartmentService: ApartmentService) {}

  onSubmit(form: NgForm) {
    const { name, rooms, price, description } = form.value;
    const newApartment = { id: uuidv4(), name, rooms: +rooms, price: +price, description };
    this.apartmentService.addApartment(newApartment);
    form.reset();
  }

}
