import { Component, EventEmitter, OnDestroy, OnInit, Output } from '@angular/core';
import { map, Observable, Subscription } from 'rxjs';
import { Apartment } from '../models/apartment.model';
import { ApartmentService } from '../services/apartment.service';
import { FormsModule } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { UpdateApartmentRequest } from '../models/update-apartment-request.model';

@Component({
  selector: 'app-apartment-list',
  standalone: true,
  imports: [FormsModule, CommonModule],
  templateUrl: './apartment-list.component.html',
  styleUrl: './apartment-list.component.css',
})
export class ApartmentListComponent implements OnInit, OnDestroy {
  editApartmentId: string | null = null;
  @Output() editApartment = new EventEmitter<string>();
  apartments$?: Observable<Apartment[]>;
  deleteApartmentSubscription: Subscription = new Subscription();
  getApartmentsSubscription: Subscription = new Subscription();
  rooms?: number;
  price: string = 'asc';
  filteredApartmentsCount: number = 0;


  constructor(private apartmentService: ApartmentService) {}
  ngOnDestroy(): void {
    this.deleteApartmentSubscription.unsubscribe();
  }

  ngOnInit(): void {
    this.loadApartments();
  }

  
  loadApartments(): void {
    this.apartments$ = this.apartmentService.getAllApartments(
      this.rooms,
      this.price
    );

    //Виводимо данні про об'єкт в консоль, щоб перевірити, чи інформація з API отримується коректно
    this.apartments$.subscribe((apartments) => {
      if (apartments.length) {
        this.filteredApartmentsCount = apartments.length;
        console.log(apartments);
        //console.log(JSON.stringify(apartments, null, 2)); // null, 2 для форматування
      }
    });
  }
  onFilterChange(event: any): void {
    this.rooms = event.target.value;
    this.loadApartments();
  }

  onEditApartment(id: string): void {
    this.editApartmentId = id;
    this.editApartment.emit(id);
  }

  onApartmentUpdated(): void {
    this.editApartmentId = null;
    this.loadApartments();
  }

  onSortChange(event: any): void {
    this.price = event.target.value;
    this.loadApartments();
  }

  deleteApartment(id: string): void {
    this.deleteApartmentSubscription = this.apartmentService
      .deleteApartment(id)
      .subscribe({
        next: (response) => {
          this.loadApartments();
          return response;
        },
        error: (err) => {
          console.error('Error deleting apartment:', err);
        },
      });
  }
  applyFilters(): void {
    this.loadApartments();
  }
}
<div class="container d-flex justify-content-center mb-5">
  <div class="card p-4 shadow-sm w-75" >
    <h3 class="text-center mb-4">Available apartments {{ filteredApartmentsCount }}</h3>

   

    <div class="mb-3 w-50">
      <div class="row">
        <div class="col sort-filter">
          <label for="price" class="form-label">Sort by:</label>
          <select id="price" class="form-select" [(ngModel)]="price" (change)="applyFilters()">
            <option value="asc">Price - lowest to highest</option>
            <option value="desc">Price - highest to lowest</option>
          </select>
        </div>
        
    
        <div class="col">
          <label for="rooms" class="form-label">Beds:</label>
          <input id="rooms" type="number" class="form-control" [(ngModel)]="rooms" min="0" (input)="applyFilters()">
        </div>
        
      </div>
    </div>
    
    <ng-container *ngIf="(apartments$ | async) as apartments">
      <ul class="list-group">
    
        <li *ngFor="let apartment of apartments" class="list-group-item d-flex justify-content-between align-items-center">
          {{ apartment?.name }} / {{ apartment?.rooms }} beds | ${{ apartment?.price }}
          
          <div class="btn-group">
            <!-- <button class="btn btn-primary btn-sm me-2" (click)="editApartment(apartment)">Edit</button> -->
            <button class="btn btn-primary btn-sm me-2" (click)="onEditApartment(apartment.id)">Edit</button>
            <button class="btn btn-danger btn-sm" (click)="deleteApartment(apartment.id)">Delete</button>
          </div>
        </li>
      </ul>
  </ng-container>

    <ng-template #noData *ngIf="!(apartments$ | async)?.length">
      <p class="text-center text-muted mt-3">No apartments available</p>
    </ng-template>
  </div>
</div>
